function init(){addListeners()}function addListeners(){$("#biometric-weight-form").submit(function(a){a.preventDefault(),$.ajax({type:"POST",url:"/api/biometrics",data:$(this).serialize(),success:function(){$("#page-body").prepend('<div class="alert alert-success">Well done fatty, now go for a run!</div>'),$("#biometric-weight-form").remove()},error:function(a){$("#page-body").prepend('<div class="alert alert-error">Err, something went wrong: '+a+"</div>")}})})}$(document).ready(init());